mapper.select:
select * from student


        sname like concat('%',#{sname},'%')


        and tid = #{tid}



    limit #{pageStart},#{pageSize}

select a.*,b.* from (select s.sname,s.sex,s.tid,s.school,s.address,m.* from student s left join (select sid,
sum(case when cid = #{course.cid} then score else 0 end) z#{course.cid}
from studentscore ss group by sid order by sid)m on s.sid=m.sid) a left join ( select sid ,max(case when type = 0 then content else 0 end) s1 ,max(case when type = 1 then content else 0 end) s2 ,max(case when type = 2 then content else 0 end) s3 ,max(case when type = 3 then content else 0 end) s4 ,max(case when type = 4 then content else 0 end) s5 from appraise group by sid) b on a.sid=b.sid
tid=#{tid}
and sname like concat('%',#{sname},'%')
limit #{pageStart},#{pageSize}

select a.*,b.* from (select s.sname,s.sex,s.tid,s.school,s.address,m.* from student s left join (select sid,
sum(case when cid = #{course.cid} then score else 0 end) z#{course.cid}
from studentscore ss group by sid order by sid)m on s.sid=m.sid) a left join ( select sid ,max(case when type = 0 then content else 0 end) s1 ,max(case when type = 1 then content else 0 end) s2 ,max(case when type = 2 then content else 0 end) s3 ,max(case when type = 3 then content else 0 end) s4 ,max(case when type = 4 then content else 0 end) s5 from appraise group by sid) b on a.sid=b.sid
tid=#{tid}
and sname like concat('%',#{sname},'%')
